import React, { useState } from "react";
import "./Login.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login() {
  const navigate = useNavigate();

  const [user, setUser] = useState({
    username: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({
      ...user,
      [name]: value,
    });
  };

  function handleSubmit(e) {
    e.preventDefault();

    axios
      .post("http://localhost:3008/login", {
        username: user.username,
        password: user.password,
      })
      .then((res) => {
        console.log(res.data, "userLogin");
        if (res.data.success) {
          // Store user information in session storage
          sessionStorage.setItem("username", user.username);
          sessionStorage.setItem("userRole", res.data.userRole);
          sessionStorage.setItem("did", res.data.did);

          navigate("/holder-home");
        } else {
          alert("Invalid Username or Password");
        }
      })
      .catch((err) => console.log(err));

    // Clear form fields after submission
    setUser({ username: "", password: "" });
  }

  return (
    <div>
      <div className="bg-dark title-bar">
        <div className="container-xxl">
          <h1 className="display-1">Soverify Solution</h1>
          <img src={img320} alt="" className="d-sm-none" />
          <img src={img480} alt="" className="d-none d-sm-block d-md-none" />
          <img src={img768} alt="" className="d-none d-md-block d-lg-none" />
          <img src={img1024} alt="" className="d-none d-lg-block d-xl-none" />
          <img src={img1280} alt="" className="d-none d-xl-block d-xxl-none" />
          <img src={img1440} alt="" className="d-none d-xxl-block" />
        </div>
      </div>
      <div>
        <form className="formDisplay" onSubmit={handleSubmit}>
          <h2>Login To Use Soverify solution</h2>
          <h3>You are One Step Away to Use Soverify</h3>

          <div className="mb-3">
            <label htmlFor="exampleInputUserName" className="form-label">
              Name
            </label>
            <input
              type="text"
              className="form-control"
              id="exampleInputUserName"
              aria-describedby="nameLabel"
              name="username"
              value={user.username}
              onChange={handleChange}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="exampleInputPassword" className="form-label">
              Password
            </label>
            <input
              type="password"
              className="form-control"
              id="exampleInputPassword"
              aria-describedby="password"
              name="password"
              value={user.password}
              onChange={handleChange}
            />
          </div>

          <button type="submit" className="btn btn-primary">
            Login
          </button>
        </form>

        <h4 style={{ textAlign: "center" }}>
          If you don't have an account{" "}
          <a href="/register" className="link-light">
            Register here
          </a>
        </h4>
      </div>
    </div>
  );
}

export default Login;
